import React, { useState, useEffect, useRef, useCallback } from 'react';
import io from 'socket.io-client';

// Load config and create socket connection
const [socket, setSocket] = useState(null);

// Connect to the WebSocket server using config
useEffect(() => {
  const loadConfigAndConnect = async () => {
    try {
      // Fetch the config file from the server
      const response = await fetch('/config.json');
      if (!response.ok) {
        throw new Error(`Failed to load config: ${response.status}`);
      }

      const config = await response.json();
      console.log('Loaded config:', config);

      // Connect to the server using the address from config
      const newSocket = io(config.client.serverAddress);
      setSocket(newSocket);

      // Set up event listeners
      newSocket.on('connect', () => {
        console.log(`Connected to server at ${config.client.serverAddress}`);
      });

      newSocket.on('init', (initialState) => {
        console.log('Received initial game state:', initialState);
        setGameState(initialState);
      });

      newSocket.on('gameState', (newState) => {
        // If we're showing gameInProgress screen, only update if returning to lobby
        if (gameState?.gameInProgress && !gameState.players[newSocket.id] && 
            newState.appPhase === 'playing') {
          // Don't update state - keep showing "Game in Progress" screen
          return;
        }

        // Otherwise update normally
        setGameState(newState);
      });

      newSocket.on('gameOver', (data) => {
        console.log('Game over with data:', data);
        setIsGameOver(true);
        setGameOverData(data);
      });
    } catch (error) {
      console.error('Error loading config:', error);
      // Fallback to localhost if config loading fails
      const fallbackSocket = io('http://localhost:3001');
      setSocket(fallbackSocket);

      // Set up the same event listeners for the fallback socket
      fallbackSocket.on('connect', () => {
        console.log('Connected to server at http://localhost:3001 (fallback)');
      });

      // ... rest of the event listeners
    }
  };

  loadConfigAndConnect();

  // Cleanup function
  return () => {
    if (socket) {
      socket.disconnect();
    }
  };
}, []);  // Empty dependency array - only run on mount

// Map of tetromino values to colors
const COLORS = {
  1: 'cyan',    // I
  2: 'blue',    // J
  3: 'orange',  // L
  4: 'yellow',  // O
  5: 'green',   // S
  6: 'purple',  // T
  7: 'red'      // Z
};

// ----- GAME BOARD COMPONENT -----
const GameBoard = ({ board, players, currentPlayerId }) => {
  const canvasRef = useRef(null);
  const cellSize = 30; // each cell is 30px

  // Calculate canvas dimensions based on board size
  const canvasWidth = board[0] ? board[0].length * cellSize : 300;
  const canvasHeight = board.length * cellSize || 600;

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');

    // Clear the canvas
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // Draw a dark background
    ctx.fillStyle = '#111';
    ctx.fillRect(0, 0, canvas.width, canvas.height);

    // Draw the grid
    ctx.strokeStyle = '#333';
    ctx.lineWidth = 0.5;

    // Draw grid lines
    for (let r = 0; r <= board.length; r++) {
      ctx.beginPath();
      ctx.moveTo(0, r * cellSize);
      ctx.lineTo(canvas.width, r * cellSize);
      ctx.stroke();
    }

    for (let c = 0; c <= board[0].length; c++) {
      ctx.beginPath();
      ctx.moveTo(c * cellSize, 0);
      ctx.lineTo(c * cellSize, canvas.height);
      ctx.stroke();
    }

    // Draw placed pieces on the board
    for (let r = 0; r < board.length; r++) {
      for (let c = 0; c < board[r].length; c++) {
        const cell = board[r][c];
        if (cell !== 0) {
          // If the cell has a complex structure (from server)
          if (typeof cell === 'object') {
            const playerId = cell.playerId;
            const player = Object.values(players).find(p => p.id === playerId.substring(0, 4));
            ctx.fillStyle = player ? player.color : 'gray';
          } else {
            // If it's just a number (simple case)
            ctx.fillStyle = COLORS[cell] || 'gray';
          }

          ctx.fillRect(c * cellSize, r * cellSize, cellSize, cellSize);
          ctx.strokeStyle = '#FFF';
          ctx.lineWidth = 1;
          ctx.strokeRect(c * cellSize, r * cellSize, cellSize, cellSize);
        }
      }
    }

    // Draw active tetromino for each player
    Object.values(players).forEach(player => {
      const { x, y, currentPiece, color } = player;

      if (!currentPiece) return;

      const shape = currentPiece.shape;

      for (let r = 0; r < shape.length; r++) {
        for (let c = 0; c < shape[r].length; c++) {
          if (shape[r][c] !== 0) {
            const boardX = x + c;
            const boardY = y + r;

            // Skip if out of bounds or above the board
            if (boardX < 0 || boardX >= board[0].length || 
                boardY < 0 || boardY >= board.length) {
              continue;
            }

            ctx.fillStyle = color || currentPiece.color || 'gray';
            ctx.fillRect(boardX * cellSize, boardY * cellSize, cellSize, cellSize);
            ctx.strokeStyle = '#FFF';
            ctx.lineWidth = 1;
            ctx.strokeRect(boardX * cellSize, boardY * cellSize, cellSize, cellSize);
          }
        }
      }
    });

  }, [board, players, currentPlayerId]);

  return (
    <div>
      <canvas
        ref={canvasRef}
        width={canvasWidth}
        height={canvasHeight}
        style={{ border: '2px solid #555' }}
      />
    </div>
  );
};

// ----- PLAYER LIST COMPONENT -----
const PlayerList = ({ players, currentPlayerId }) => {
  const currentPlayerShortId = currentPlayerId?.substring(0, 4);

  return (
    <div style={{ marginBottom: '20px' }}>
      <h3 style={{ marginBottom: '10px' }}>Players</h3>

      <div style={{ 
        display: 'flex', 
        flexDirection: 'column',
        gap: '10px',
        maxWidth: '250px'
      }}>
        {Object.values(players).map(player => {
          const isCurrentPlayer = player.id === currentPlayerShortId;

          return (
            <div 
              key={player.id}
              style={{ 
                display: 'flex',
                alignItems: 'center',
                padding: '8px',
                backgroundColor: '#333',
                borderRadius: '4px',
                borderLeft: `4px solid ${player.color}`,
              }}
            >
              <div 
                style={{ 
                  width: '15px', 
                  height: '15px', 
                  backgroundColor: player.color,
                  marginRight: '10px',
                  borderRadius: '3px'
                }}
              />

              <div style={{ flex: 1 }}>
                Player {player.playerNumber}
                {isCurrentPlayer && ' (You)'}
              </div>

              <div style={{ 
                marginLeft: 'auto', 
                fontWeight: 'bold', 
                fontSize: '14px',
                color: '#AAA'
              }}>
                {player.score}
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

// ----- SCOREBOARD COMPONENT -----
const ScoreBoard = ({ players }) => {
  // Sort players by score in descending order
  const sortedPlayers = [...Object.values(players)].sort((a, b) => b.score - a.score);

  return (
    <div>
      <h3 style={{ marginBottom: '10px' }}>Scores</h3>

      <div style={{
        backgroundColor: '#222',
        padding: '10px',
        borderRadius: '4px',
        maxWidth: '250px'
      }}>
        {sortedPlayers.map(player => (
          <div 
            key={player.id}
            style={{
              display: 'flex',
              justifyContent: 'space-between',
              padding: '5px 0',
              borderBottom: '1px solid #444'
            }}
          >
            <div style={{ display: 'flex', alignItems: 'center' }}>
              <div 
                style={{ 
                  width: '10px', 
                  height: '10px', 
                  backgroundColor: player.color,
                  marginRight: '8px',
                  borderRadius: '2px'
                }}
              />
              <span>Player {player.playerNumber}</span>
            </div>
            <div style={{ fontWeight: 'bold' }}>{player.score}</div>
          </div>
        ))}
      </div>
    </div>
  );
};

// ----- HOME SCREEN COMPONENT -----
const HomeScreen = ({ 
  players, 
  currentPlayerId, 
  readyPlayers, 
  onReady, 
  onStartGame, 
  onSetGameMode, 
  gameMode,
  gameInProgress,
  isRejoining
}) => {
  const currentPlayerShortId = currentPlayerId ? currentPlayerId.substring(0, 4) : null;
  const currentPlayer = currentPlayerShortId ? Object.values(players).find(p => p.id === currentPlayerShortId) : null;
  const isPlayerOne = currentPlayer && currentPlayer.playerNumber === 1;

  const gameModes = [
    { id: 'classic', name: 'Classic Mode', disabled: false },
    { id: 'battle', name: 'Battle Mode', disabled: true },
    { id: 'cooperative', name: 'Co-op Mode', disabled: true }
  ];

  return (
    <div style={{ textAlign: 'center', maxWidth: '800px', margin: '0 auto' }}>
      <h1>Tetristributed</h1>

      {gameInProgress && (
        <div style={{
          backgroundColor: '#552233',
          padding: '10px',
          borderRadius: '5px',
          marginBottom: '20px'
        }}>
          <h2>Game In Progress</h2>
          <p>A game is currently in progress. Please wait for it to finish.</p>
        </div>
      )}

      <div style={{ 
        display: 'flex', 
        justifyContent: 'center', 
        marginBottom: '40px',
        flexWrap: 'wrap',
        gap: '20px'
      }}>
        {[1, 2, 3, 4].map(num => {
          const player = Object.values(players).find(p => p.playerNumber === num);
          const isReady = player && readyPlayers.some(id => players[id] && players[id].id === player.id);
          const isCurrentPlayer = player && currentPlayerShortId && player.id === currentPlayerShortId;
          const isPlayerRejoining = isCurrentPlayer && player.isRejoining;
          const bgColor = !player ? '#222' : '#444';

          return (
            <div key={num} style={{
              width: '150px',
              height: '100px',
              border: '2px solid #555',
              borderRadius: '8px',
              padding: '10px',
              backgroundColor: bgColor,
              position: 'relative',
              display: 'flex',
              flexDirection: 'column'
            }}>
              <div style={{ fontWeight: 'bold', marginBottom: '5px' }}>
                Player {num}
                {isCurrentPlayer && ' (You)'}
              </div>

              <div style={{ flex: 1 }}>
                {player && isReady && (
                  <div style={{
                    width: '20px',
                    height: '20px',
                    backgroundColor: player.color,
                    margin: '0 auto'
                  }}></div>
                )}

                {isPlayerRejoining && (
                  <div style={{
                    position: 'absolute',
                    top: '0',
                    left: '0',
                    right: '0',
                    padding: '2px',
                    backgroundColor: '#33AA33',
                    color: 'white',
                    fontSize: '12px',
                    borderTopLeftRadius: '6px',
                    borderTopRightRadius: '6px'
                  }}>
                    Welcome Back!
                  </div>
                )}

                {!player && <div>Waiting for player...</div>}

                {player && !isReady && !isCurrentPlayer && (
                  <div style={{ fontSize: '14px', color: '#888' }}>Not Ready</div>
                )}
              </div>

              {isCurrentPlayer && (
                <div style={{ marginTop: 'auto' }}>
                  <button 
                    onClick={() => onReady(!isReady)}
                    style={{
                      backgroundColor: isReady ? '#333' : '#FF3333',
                      border: 'none',
                      color: 'white',
                      padding: '5px 10px',
                      borderRadius: '4px',
                      cursor: 'pointer',
                      width: '100%'
                    }}
                  >
                    {isReady ? 'Cancel' : isPlayerRejoining ? 'Rejoin Game' : 'Press X'}
                  </button>
                </div>
              )}
            </div>
          );
        })}
      </div>

      {isPlayerOne && (
        <div style={{ marginBottom: '30px' }}>
          <h2>Game Mode</h2>
          <div style={{ display: 'flex', justifyContent: 'center', gap: '15px' }}>
            {gameModes.map(mode => (
              <button
                key={mode.id}
                onClick={() => !mode.disabled && onSetGameMode(mode.id)}
                style={{
                  padding: '10px 15px',
                  backgroundColor: gameMode === mode.id ? '#FF5733' : '#444',
                  border: 'none',
                  borderRadius: '4px',
                  color: 'white',
                  opacity: mode.disabled ? 0.5 : 1,
                  cursor: mode.disabled ? 'not-allowed' : 'pointer'
                }}
                disabled={mode.disabled}
              >
                {mode.name}
                {mode.disabled && ' (Coming Soon)'}
              </button>
            ))}
          </div>
        </div>
      )}

      {isPlayerOne && (
        <button
          onClick={onStartGame}
          style={{
            padding: '12px 30px',
            backgroundColor: readyPlayers.length > 0 ? '#33FF57' : '#555',
            border: 'none',
            borderRadius: '4px',
            color: 'white',
            fontSize: '18px',
            cursor: readyPlayers.length > 0 ? 'pointer' : 'not-allowed'
          }}
          disabled={readyPlayers.length === 0}
        >
          Start Game
        </button>
      )}

      {!isPlayerOne && readyPlayers.length > 0 && (
        <div style={{ marginTop: '20px', fontSize: '18px' }}>
          Waiting for Player 1 to start the game...
        </div>
      )}

      <div style={{ marginTop: '40px' }}>
        <h3>Controls</h3>
        <p>← → : Move left/right | ↑ or Z : Rotate | ↓ : Soft drop | Space : Hard drop</p>
      </div>
    </div>
  );
};

// ----- GAME OVER SCREEN COMPONENT -----
const GameOverScreen = ({ gameOverData, onTimeout }) => {
  const [countdown, setCountdown] = useState(5);

  useEffect(() => {
    // Start countdown
    const timer = setInterval(() => {
      setCountdown(prev => {
        if (prev <= 1) {
          clearInterval(timer);
          onTimeout();
          return 0;
        }
        return prev - 1;
      });
    }, 1000);

    return () => clearInterval(timer);
  }, [onTimeout]);

  // Format the player ID to be more readable
  const playerShortId = gameOverData.playerId ? gameOverData.playerId.substring(0, 4) : 'Unknown';

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: 'rgba(0, 0, 0, 0.8)',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      zIndex: 1000
    }}>
      <div style={{
        backgroundColor: '#333',
        padding: '30px',
        borderRadius: '10px',
        boxShadow: '0 0 20px rgba(255, 0, 0, 0.5)',
        textAlign: 'center',
        color: 'white',
        maxWidth: '400px'
      }}>
        <h2 style={{ margin: '0 0 20px', fontSize: '28px', color: '#FF5733' }}>Game Over</h2>

        <div style={{ fontSize: '18px', marginBottom: '15px' }}>
          Player <span style={{ fontWeight: 'bold', color: '#FF5733' }}>{playerShortId}</span> lost!
        </div>

        <div style={{ fontSize: '24px', margin: '20px 0' }}>
          Final Score: <span style={{ fontWeight: 'bold' }}>{gameOverData.score}</span>
        </div>

        <div style={{ fontSize: '16px', marginTop: '25px', color: '#AAA' }}>
          Returning to lobby in {countdown} seconds...
        </div>
      </div>
    </div>
  );
};

// ----- MAIN APP COMPONENT -----
function App() {
  const [gameState, setGameState] = useState(null);
  const [isGameOver, setIsGameOver] = useState(false);
  const [gameOverData, setGameOverData] = useState(null);

  // Handle socket events
  useEffect(() => {
    socket.on('connect', () => {
      console.log('Connected to server');
    });

    socket.on('init', (initialState) => {
      console.log('Received initial game state:', initialState);
      setGameState(initialState);
    });

    socket.on('gameState', (newState) => {
      // If we're showing gameInProgress screen, only update if returning to lobby
      if (gameState?.gameInProgress && !gameState.players[socket.id] && 
          newState.appPhase === 'playing') {
        // Don't update state - keep showing "Game in Progress" screen
        return;
      }

      // Otherwise update normally
      setGameState(newState);
    });

    socket.on('gameOver', (data) => {
      console.log('Game over with data:', data);
      setIsGameOver(true);
      setGameOverData(data);
    });

    return () => {
      socket.off('connect');
      socket.off('init');
      socket.off('gameState');
      socket.off('gameOver');
    };
  }, []);

  // Keyboard control handlers
  useEffect(() => {
    const handleKeyDown = (e) => {
      // Only process key events if game is in progress
      if (gameState && gameState.appPhase === 'playing') {
        switch (e.code) {
          case 'ArrowLeft':
            // First send immediate move, then start DAS
            socket.emit('playerAction', { type: 'moveLeft' });
            socket.emit('playerAction', { type: 'startDAS', direction: 'left' });
            break;
          case 'ArrowRight':
            // First send immediate move, then start DAS
            socket.emit('playerAction', { type: 'moveRight' });
            socket.emit('playerAction', { type: 'startDAS', direction: 'right' });
            break;
          case 'ArrowUp':
          case 'KeyZ':
            socket.emit('playerAction', { type: 'rotate' });
            break;
          case 'ArrowDown':
            socket.emit('playerAction', { type: 'softDrop' });
            break;
          case 'Space':
            socket.emit('playerAction', { type: 'hardDrop' });
            break;
          default:
            break;
        }
      }
    };

    const handleKeyUp = (e) => {
      // Only process key events if game is in progress
      if (gameState && gameState.appPhase === 'playing') {
        switch (e.code) {
          case 'ArrowLeft':
          case 'ArrowRight':
            socket.emit('playerAction', { type: 'endDAS' });
            break;
          case 'ArrowDown':
            socket.emit('playerAction', { type: 'endSoftDrop' });
            break;
          default:
            break;
        }
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('keyup', handleKeyUp);

    return () => {
      window.removeEventListener('keydown', handleKeyDown);
      window.removeEventListener('keyup', handleKeyUp);
    };
  }, [gameState]);

  // Handle player ready state
  const handlePlayerReady = useCallback((isReady) => {
    socket.emit('playerReady', isReady);
  }, []);

  // Handle game start
  const handleStartGame = useCallback(() => {
    socket.emit('startGame');
  }, []);

  // Handle game mode change
  const handleSetGameMode = useCallback((mode) => {
    socket.emit('setGameMode', mode);
  }, []);

  // Handle game over timeout
  const handleGameOverTimeout = useCallback(() => {
    setIsGameOver(false);
    setGameOverData(null);
  }, []);

  // Show loading screen if no game state
  if (!gameState) {
    return <div className="App"><h1>Connecting to server...</h1></div>;
  }

  // Home screen or game screen based on app phase
  return (
    <div className="App">
      {gameState.appPhase === 'homescreen' && (
        <HomeScreen
          players={gameState.players}
          currentPlayerId={socket.id}
          readyPlayers={gameState.readyPlayers}
          onReady={handlePlayerReady}
          onStartGame={handleStartGame}
          onSetGameMode={handleSetGameMode}
          gameMode={gameState.gameMode}
          gameInProgress={gameState.gameInProgress}
          isRejoining={gameState.players[socket.id]?.isRejoining}
        />
      )}

      {gameState.appPhase === 'playing' && (
        <>
          <h1>Tetristributed</h1>
          <p>Connected as player: {socket.id && socket.id.substring(0, 4)}</p>
          <div style={{ display: 'flex', gap: '20px', marginBottom: '20px' }}>
            <GameBoard 
              board={gameState.board} 
              players={gameState.players}
              currentPlayerId={socket.id}
            />
            <div>
              <PlayerList players={gameState.players} currentPlayerId={socket.id} />
              <ScoreBoard players={gameState.players} />
            </div>
          </div>
        </>
      )}

      {isGameOver && gameOverData && (
        <GameOverScreen 
          gameOverData={gameOverData} 
          onTimeout={handleGameOverTimeout} 
        />
      )}
    </div>
  );
}

export default App;
